// src/components/Modals/ListaPermisos.js
import React, { useEffect, useMemo, useState } from 'react';
import { Check } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { PERMISOS as PERMISOS_LOCAL, ETIQUETAS_PERMISOS } from '../../constants/permissions';

const ListaPermisos = ({ value = [], onChange, columnas = 2, disabled = false }) => {
  const { authFetch } = useAuth();
  const [catalogo, setCatalogo] = useState(PERMISOS_LOCAL);

  useEffect(() => {
    let cancelado = false;
    const cargar = async () => {
      try {
        const resp = await authFetch('http://localhost:5000/api/permissions');
        if (!resp.ok) return; // fallback a local
        const data = await resp.json();
        if (!cancelado && Array.isArray(data.permissions)) {
          setCatalogo(data.permissions);
        }
      } catch (_) {
        // fallback silencioso a constantes locales
      }
    };
    cargar();
    return () => { cancelado = true; };
  }, [authFetch]);

  const lista = useMemo(() => catalogo.map((id) => ({ id, nombre: ETIQUETAS_PERMISOS[id] || id })), [catalogo]);

  const toggle = (id) => {
    if (disabled) return;
    if (value.includes(id)) onChange?.(value.filter((x) => x !== id));
    else onChange?.([...(value || []), id]);
  };

  return (
    <div className={`grid grid-cols-1 md:grid-cols-${columnas} gap-2`}>
      {lista.map((perm) => (
        <div
          key={perm.id}
          className={`p-3 border-2 rounded-lg cursor-pointer transition-all ${
            value.includes(perm.id) ? 'border-blue-500 bg-blue-50' : 'border-gray-200 hover:border-gray-300'
          } ${disabled ? 'opacity-60 cursor-not-allowed' : ''}`}
          onClick={() => toggle(perm.id)}
        >
          <div className="flex items-start gap-3">
            <div className="flex-shrink-0 mt-1">
              <div
                className={`w-5 h-5 rounded border-2 flex items-center justify-center ${
                  value.includes(perm.id) ? 'bg-blue-600 border-blue-600' : 'border-gray-300'
                }`}
              >
                {value.includes(perm.id) && <Check className="w-3 h-3 text-white" />}
              </div>
            </div>
            <div className="flex-1">
              <p className="font-medium text-gray-900">{perm.nombre}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ListaPermisos;
