import React, { useEffect, useMemo, useRef, useState } from 'react';
import { Shield, Plus, Edit, Trash2, Loader, Search } from 'lucide-react';
import PageHeader from '../../components/Common/PageHeader';
import Card from '../../components/Common/Card';
import { useAuth } from '../../contexts/AuthContext';
import { PERMISOS, ETIQUETAS_PERMISOS } from '../../constants/permissions';

const RolesPage = () => {
    const { authFetch } = useAuth();

    const [roles, setRoles] = useState([]);
    const [cargando, setCargando] = useState(true);
    const [error, setError] = useState('');
    const [busqueda, setBusqueda] = useState('');

    const [modalAbierto, setModalAbierto] = useState(false);
    const [modoModal, setModoModal] = useState('crear'); // 'crear' | 'editar'
    const [rolSeleccionado, setRolSeleccionado] = useState(null);
    const [form, setForm] = useState({ nombre: '', descripcion: '', permisos: [], estado: 'Activo' });
    const [guardando, setGuardando] = useState(false);

    const didFetchRef = useRef(false);

    useEffect(() => {
        const cargarRoles = async () => {
            try {
                setCargando(true);
                // Sincronizar roles usados en usuarios
                await authFetch('http://localhost:5000/api/roles/sync', { method: 'POST' });
                const resp = await authFetch('http://localhost:5000/api/roles');
                if (!resp.ok) throw new Error('Error al obtener roles');
                const data = await resp.json();
                setRoles(data.roles || []);
            } catch (e) {
                setError(e.message || 'Error al cargar roles');
            } finally {
                setCargando(false);
            }
        };
        if (didFetchRef.current) return;
        didFetchRef.current = true;
        cargarRoles();
    }, []);

    const rolesFiltrados = useMemo(() => {
        const q = busqueda.trim().toLowerCase();
        if (!q) return roles;
        return roles.filter(r =>
            r.name.toLowerCase().includes(q) ||
            (r.description || '').toLowerCase().includes(q)
        );
    }, [roles, busqueda]);

    const abrirCrear = () => {
        setModoModal('crear');
        setRolSeleccionado(null);
        setForm({ nombre: '', descripcion: '', permisos: [], estado: 'Activo' });
        setModalAbierto(true);
    };

    const abrirEditar = (rol) => {
        setModoModal('editar');
        setRolSeleccionado(rol);
        setForm({
            nombre: rol.name || '',
            descripcion: rol.description || '',
            permisos: rol.permissions || [],
            estado: rol.status || 'Activo'
        });
        setModalAbierto(true);
    };

    const cerrarModal = () => {
        setModalAbierto(false);
        setGuardando(false);
        setError('');
    };

    const crearRol = async () => {
        try {
            setGuardando(true);
            const resp = await authFetch('http://localhost:5000/api/roles', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: form.nombre,
                    description: form.descripcion,
                    permissions: form.permisos,
                    status: form.estado
                })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al crear rol');
            }
            const data = await resp.json();
            setRoles(prev => [data.role, ...prev]);
            cerrarModal();
        } catch (e) {
            setError(e.message);
        } finally {
            setGuardando(false);
        }
    };

    const editarRol = async () => {
        if (!rolSeleccionado) return;
        try {
            setGuardando(true);
            const resp = await authFetch(`http://localhost:5000/api/roles/${rolSeleccionado.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: form.nombre,
                    description: form.descripcion,
                    permissions: form.permisos,
                    status: form.estado
                })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al actualizar rol');
            }
            const data = await resp.json();
            setRoles(prev => prev.map(r => r.id === rolSeleccionado.id ? data.role : r));
            cerrarModal();
        } catch (e) {
            setError(e.message);
        } finally {
            setGuardando(false);
        }
    };

    const eliminarRol = async (rolId) => {
        if (!window.confirm('Â¿Eliminar este rol?')) return;
        try {
            const resp = await authFetch(`http://localhost:5000/api/roles/${rolId}`, { method: 'DELETE' });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al eliminar rol');
            }
            setRoles(prev => prev.filter(r => r.id !== rolId));
        } catch (e) {
            alert(e.message);
        }
    };

    const cambiarEstado = async (rol) => {
        try {
            const nuevo = rol.status === 'Activo' ? 'Inactivo' : 'Activo';
            const resp = await authFetch(`http://localhost:5000/api/roles/${rol.id}/status`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status: nuevo })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al cambiar estado');
            }
            const data = await resp.json();
            setRoles(prev => prev.map(r => r.id === rol.id ? data.role : r));
        } catch (e) {
            alert(e.message);
        }
    };

    if (cargando) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center">
                    <Loader className="w-12 h-12 animate-spin mx-auto mb-4" />
                    <p className="text-gray-600">Cargando roles...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center text-red-600">
                    <p>Error: {error}</p>
                    <button onClick={() => window.location.reload()} className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg">
                        Reintentar
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <PageHeader
                title="GestiÃ³n de Roles"
                subtitle="Administra los roles y permisos del sistema"
                icon={Shield}
            >
                <button onClick={abrirCrear} className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-xl font-medium flex items-center gap-2 hover:from-blue-700 hover:to-purple-700 transition-all">
                    <Plus className="w-4 h-4" />
                    Nuevo Rol
                </button>
            </PageHeader>

            <Card>
                {/* Buscador */}
                <div className="flex flex-col sm:flex-row gap-4 mb-4">
                    <div className="flex-1 relative">
                        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <input
                            type="text"
                            placeholder="Buscar roles..."
                            value={busqueda}
                            onChange={(e) => setBusqueda(e.target.value)}
                            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        />
                    </div>
                </div>
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead>
                            <tr className="border-b border-gray-200">
                                <th className="text-left py-2 px-4 font-semibold text-gray-900">Nombre</th>
                                <th className="text-left py-2 px-4 font-semibold text-gray-900">DescripciÃ³n</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Permisos</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Estado</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {rolesFiltrados.length ? rolesFiltrados.map((rol) => (
                                <tr key={rol.id} className="border-b border-gray-100">
                                    <td className="py-2 px-4">{rol.name}</td>
                                    <td className="py-2 px-4 text-gray-600">{rol.description || '-'}</td>
                                    <td className="py-2 px-4 text-center">{(rol.permissions || []).length}</td>
                                    <td className="py-2 px-4 text-center">
                                        <div className="flex flex-col items-center gap-1">
                                            <span className={`px-2 py-0.5 rounded-lg text-xs font-medium whitespace-nowrap ${rol.status === 'Activo' ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}>
                                                {rol.status}
                                            </span>
                                            <button
                                                className={`px-2 py-1 rounded-lg text-white text-xs font-medium transition whitespace-nowrap ${rol.status === 'Activo' ? 'bg-red-500 hover:bg-red-600' : 'bg-blue-500 hover:bg-blue-600'}`}
                                                onClick={() => cambiarEstado(rol)}
                                            >
                                                {rol.status === 'Activo' ? 'Dar Baja' : 'Dar Alta'}
                                            </button>
                                        </div>
                                    </td>
                                    <td className="py-2 px-4">
                                        <div className="flex items-center justify-center gap-2">
                                            <button onClick={() => abrirEditar(rol)} className="px-3 py-1 rounded-lg border text-sm flex items-center gap-1">
                                                <Edit className="w-4 h-4" /> Editar
                                            </button>
                                            <button onClick={() => eliminarRol(rol.id)} className="px-3 py-1 rounded-lg border text-sm flex items-center gap-1 text-red-600 border-red-300">
                                                <Trash2 className="w-4 h-4" /> Eliminar
                                            </button>
                                        </div>
                                    </td>
                                </tr>
                            )) : (
                                <tr>
                                    <td colSpan={5} className="text-center py-6 text-gray-500">No se encontraron roles</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </Card>

            {modalAbierto && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999]">
                    <div className="bg-white p-6 rounded-2xl shadow-lg text-left max-w-lg w-full">
                        <h2 className="text-lg font-semibold mb-4">{modoModal === 'crear' ? 'Nuevo Rol' : 'Editar Rol'}</h2>
                        {error && <div className="mb-3 p-2 bg-red-100 text-red-800 rounded">{error}</div>}
                        <div className="space-y-3">
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">Nombre *</label>
                                <input value={form.nombre} onChange={e => setForm(f => ({...f, nombre: e.target.value}))} className="w-full px-3 py-2 border rounded-lg" />
                            </div>
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">DescripciÃ³n</label>
                                <input value={form.descripcion} onChange={e => setForm(f => ({...f, descripcion: e.target.value}))} className="w-full px-3 py-2 border rounded-lg" />
                            </div>
                            <div>
                                <label className="block text-sm text-gray-600 mb-2">Permisos</label>
                                <div className="grid grid-cols-2 gap-2">
                                    {PERMISOS.map(p => (
                                        <label key={p} className="flex items-center gap-2 text-sm">
                                            <input
                                                type="checkbox"
                                                checked={form.permisos.includes(p)}
                                                onChange={(e) => {
                                                    const checked = e.target.checked;
                                                    setForm(f => ({
                                                        ...f,
                                                        permisos: checked ? [...f.permisos, p] : f.permisos.filter(x => x !== p)
                                                    }));
                                                }}
                                            />
                                            <span>{ETIQUETAS_PERMISOS[p] || p}</span>
                                        </label>
                                    ))}
                                </div>
                            </div>
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">Estado</label>
                                <select value={form.estado} onChange={e => setForm(f => ({...f, estado: e.target.value}))} className="w-full px-3 py-2 border rounded-lg">
                                    <option>Activo</option>
                                    <option>Inactivo</option>
                                </select>
                            </div>
                        </div>
                        <div className="flex justify-end gap-2 mt-5">
                            <button onClick={cerrarModal} className="px-4 py-2 border rounded-lg">Cancelar</button>
                            <button disabled={guardando} onClick={modoModal === 'crear' ? crearRol : editarRol} className="px-4 py-2 bg-blue-600 text-white rounded-lg disabled:opacity-50">
                                {guardando ? 'Guardando...' : (modoModal === 'crear' ? 'Crear' : 'Guardar')}
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default RolesPage;
