// src/components/Common/InsigniaEstado.js
import React from 'react';

const InsigniaEstado = ({ estado }) => {
  const activo = String(estado).toLowerCase() === 'activo';
  const clases = activo
    ? 'bg-green-100 text-green-700'
    : 'bg-gray-100 text-gray-700';
  const label = activo ? 'Activo' : 'Inactivo';
  return (
    <span className={`px-2 py-0.5 rounded-lg text-xs font-medium whitespace-nowrap ${clases}`}>
      {label}
    </span>
  );
};

export default InsigniaEstado;
