import React, { createContext, useContext, useEffect, useState, useRef } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sessionExpiring, setSessionExpiring] = useState(false);
  //const [countdown, setCountdown] = useState(180); // 3 minutos
  const [countdown, setCountdown] = useState(10); // 10 segundos (solo prueba)
  //
  const countdownRef = useRef(null);
  const inactivityTimerRef = useRef(null);
  const totalSessionTimerRef = useRef(null);

  // ========================
  // üü¢ Inicializaci√≥n de sesi√≥n
  // ========================
  useEffect(() => {
    const initializeAuth = async () => {
      const savedUser = localStorage.getItem('user');
      const accessToken = localStorage.getItem('access_token');

      if (savedUser && accessToken) {
        setUser(JSON.parse(savedUser));

        try {
          const response = await fetch('http://localhost:5000/api/users/profile', {
            method: 'GET',
            headers: { 'Authorization': `Bearer ${accessToken}` },
          });
          if (!response.ok) await refreshToken();
        } catch (error) {
          console.error('Error verificando token:', error);
          await logout();
        }
      }
      setLoading(false);
    };

    initializeAuth();
  }, []);

  // ========================
  // üîÅ Sincronizar logout entre pesta√±as
  // ========================
  useEffect(() => {
    const syncLogout = (e) => {
      if (e.key === 'logout_event') {
        setUser(null);
        localStorage.removeItem('user');
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
      }
    };
    window.addEventListener('storage', syncLogout);
    return () => window.removeEventListener('storage', syncLogout);
  }, []);

  // ========================
  // ‚è∞ Control de inactividad
  // ========================
  useEffect(() => {
    if (!user) return;

    const resetInactivityTimers = () => {
      clearTimeout(inactivityTimerRef.current);
      clearTimeout(totalSessionTimerRef.current);
      clearInterval(countdownRef.current);
      setSessionExpiring(false);
      setCountdown(180);

      // ‚öôÔ∏è Despu√©s de 7 minutos ‚Üí mostrar aviso de expiraci√≥n
      //inactivityTimerRef.current = setTimeout(() => {
      //  setSessionExpiring(true);
      //  startCountdown();
      //}, 7 * 60 * 1000);
      // ‚öôÔ∏è Despu√©s de 10 minutos ‚Üí cerrar sesi√≥n autom√°ticamente
      //totalSessionTimerRef.current = setTimeout(() => {
      //  handleAutoLogout();
      //}, 10 * 60 * 1000);

      // ‚öôÔ∏è Despu√©s de 20 segundos ‚Üí mostrar aviso de expiraci√≥n (solo pruebas)
      inactivityTimerRef.current = setTimeout(() => {
        setSessionExpiring(true);
        startCountdown();
      }, 20 * 1000);

      // ‚öôÔ∏è Despu√©s de 30 segundos ‚Üí cerrar sesi√≥n autom√°ticamente (solo pruebas)
      totalSessionTimerRef.current = setTimeout(() => {
        handleAutoLogout();
      }, 30 * 1000);



    };

    const startCountdown = () => {
      let remaining = 180;
      countdownRef.current = setInterval(() => {
        remaining -= 1;
        setCountdown(remaining);
        if (remaining <= 0) {
          clearInterval(countdownRef.current);
        }
      }, 1000);
    };

    const handleAutoLogout = async () => {
      clearInterval(countdownRef.current);
      setSessionExpiring(false);
      await logout();
    };

    // Escuchar actividad del usuario
    window.addEventListener('mousemove', resetInactivityTimers);
    window.addEventListener('keydown', resetInactivityTimers);
    window.addEventListener('scroll', resetInactivityTimers);
    window.addEventListener('click', resetInactivityTimers);
    resetInactivityTimers();

    return () => {
      window.removeEventListener('mousemove', resetInactivityTimers);
      window.removeEventListener('keydown', resetInactivityTimers);
      window.removeEventListener('scroll', resetInactivityTimers);
      window.removeEventListener('click', resetInactivityTimers);
      clearTimeout(inactivityTimerRef.current);
      clearTimeout(totalSessionTimerRef.current);
      clearInterval(countdownRef.current);
    };
  }, [user]);

  // ========================
  // üîÅ Extender sesi√≥n
  // ========================
  const extendSession = () => {
    setSessionExpiring(false);
    setCountdown(180);
    // reinicia los contadores
    const event = new Event('mousemove');
    window.dispatchEvent(event);
  };

  // ========================
  // üîê Funciones de autenticaci√≥n
  // ========================
  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('access_token', data.access_token);
        localStorage.setItem('refresh_token', data.refresh_token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        return { success: true, user: data.user };
      } else {
        return { success: false, error: data.error || 'Error en el login' };
      }
    } catch (error) {
      return { success: false, error: 'Error de conexi√≥n con el servidor' };
    }
  };

  const logout = async () => {
    try {
      const token = localStorage.getItem('access_token');
      if (token) {
        await fetch('http://localhost:5000/api/auth/logout', {
          method: 'POST',
          headers: { 'Authorization': `Bearer ${token}` },
        });
      }
    } catch (error) {
      console.error('Error durante logout:', error);
    } finally {
      setUser(null);
      localStorage.removeItem('user');
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      localStorage.setItem('logout_event', Date.now());
    }
  };

  const refreshToken = async () => {
    try {
      const refreshToken = localStorage.getItem('refresh_token');
      if (!refreshToken) {
        await logout();
        return false;
      }

      const response = await fetch('http://localhost:5000/api/auth/refresh', {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${refreshToken}` },
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('access_token', data.access_token);
        if (data.refresh_token) {
          localStorage.setItem('refresh_token', data.refresh_token);
        }
        if (data.user) {
          setUser(data.user);
          localStorage.setItem('user', JSON.stringify(data.user));
        }
        return true;
      } else {
        await logout();
        return false;
      }
    } catch (error) {
      console.error('Error refrescando token:', error);
      await logout();
      return false;
    }
  };

  const hasPermission = (permission) => {
    if (!user || !user.permissions) return false;
    return user.permissions.includes(permission);
  };

  const authFetch = async (url, options = {}) => {
    let token = localStorage.getItem('access_token');
    const config = {
      ...options,
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
        'Authorization': `Bearer ${token}`,
      },
    };

    let response = await fetch(url, config);

    if (response.status === 401) {
      const refreshed = await refreshToken();
      if (refreshed) {
        token = localStorage.getItem('access_token');
        config.headers.Authorization = `Bearer ${token}`;
        response = await fetch(url, config);
      }
    }

    return response;
  };

  // ========================
  // Render y modal
  // ========================
  return (
    <AuthContext.Provider value={{
      user,
      login,
      logout,
      hasPermission,
      loading,
      authFetch,
      sessionExpiring,
      countdown,
      extendSession
    }}>
      {children}

      {/* üîî Modal de expiraci√≥n */}
      {sessionExpiring && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999]">
          <div className="bg-white p-6 rounded-2xl shadow-lg text-center max-w-sm">
            <h2 className="text-lg font-semibold mb-2 text-gray-800">Sesi√≥n a punto de expirar</h2>
            <p className="text-gray-600 mb-4">
              Tu sesi√≥n se cerrar√° autom√°ticamente en <b>{countdown}s</b> por inactividad.
            </p>
            <div className="flex justify-center gap-4">
              <button
                onClick={extendSession}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                Mantener sesi√≥n
              </button>
              <button
                onClick={logout}
                className="px-4 py-2 bg-gray-300 text-gray-800 rounded-lg hover:bg-gray-400"
              >
                Cerrar ahora
              </button>
            </div>
          </div>
        </div>
      )}
    </AuthContext.Provider>
  );
};
