import React, { useEffect, useMemo, useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { Shield, Plus, Edit, Trash2, Loader, Search } from 'lucide-react';
import PageHeader from '../../components/Common/PageHeader';
import Card from '../../components/Common/Card';
import { useAuth } from '../../contexts/AuthContext';
import ActionButton from '../../components/Common/ActionButton';
import ModalCrudGenerico from '../../components/Modals/ModalCrudGenerico.js';
import ListaPermisos from '../../components/Form/ListaPermisos.js';
import InsigniaEstado from '../../components/Common/InsigniaEstado';
import DialogoConfirmacion from '../../components/Common/DialogoConfirmacion';

const RolesPage = () => {
    const { authFetch } = useAuth();

    const [roles, setRoles] = useState([]);
    const [cargando, setCargando] = useState(true);
    const [error, setError] = useState('');
    const [busqueda, setBusqueda] = useState('');

    const [modalAbierto, setModalAbierto] = useState(false);
    const [modoModal, setModoModal] = useState('crear'); // 'crear' | 'editar'
    const [rolSeleccionado, setRolSeleccionado] = useState(null);
    const [valoresIniciales, setValoresIniciales] = useState({ nombre: '', descripcion: '', permisos: [], estado: 'Activo' });
    const [confirmOpen, setConfirmOpen] = useState(false);
    const [deleteTarget, setDeleteTarget] = useState(null);

    const didFetchRef = useRef(false);

    useEffect(() => {
        const cargarRoles = async () => {
            try {
                setCargando(true);
                // Sincronizar roles usados en usuarios
                await authFetch('http://localhost:5000/api/roles/sync', { method: 'POST' });
                const resp = await authFetch('http://localhost:5000/api/roles');
                if (!resp.ok) throw new Error('Error al obtener roles');
                const data = await resp.json();
                setRoles(data.roles || []);
            } catch (e) {
                setError(e.message || 'Error al cargar roles');
            } finally {
                setCargando(false);
            }
        };
        if (didFetchRef.current) return;
        didFetchRef.current = true;
        cargarRoles();
    }, []);

    const rolesFiltrados = useMemo(() => {
        const q = busqueda.trim().toLowerCase();
        if (!q) return roles;
        return roles.filter(r =>
            r.name.toLowerCase().includes(q) ||
            (r.description || '').toLowerCase().includes(q)
        );
    }, [roles, busqueda]);

    const abrirCrear = () => {
        setModoModal('crear');
        setRolSeleccionado(null);
        setValoresIniciales({ nombre: '', descripcion: '', permisos: [], estado: 'Activo' });
        setModalAbierto(true);
    };

    const abrirEditar = (rol) => {
        setModoModal('editar');
        setRolSeleccionado(rol);
        const permisosNormalizados = Array.isArray(rol.permissions)
            ? rol.permissions.map((p) => {
                if (typeof p === 'string') return p;
                if (p && typeof p === 'object') return p.id || p.name || '';
                return '';
              }).filter(Boolean)
            : [];
        setValoresIniciales({
            nombre: rol.name || '',
            descripcion: rol.description || '',
            permisos: permisosNormalizados,
            estado: rol.status || 'Activo'
        });
        setModalAbierto(true);
    };

    const cerrarModal = () => {
        setModalAbierto(false);
        setError('');
    };

    const crearRol = async (vals) => {
        try {
            const resp = await authFetch('http://localhost:5000/api/roles', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: vals.nombre,
                    description: vals.descripcion,
                    permissions: vals.permisos,
                    status: vals.estado
                })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al crear rol');
            }
            const data = await resp.json();
            setRoles(prev => [data.role, ...prev]);
            return { success: true };
        } catch (e) {
            return { success: false, error: e.message };
        }
    };

    const editarRol = async (vals) => {
        if (!rolSeleccionado) return { success: false, error: 'Rol no seleccionado' };
        try {
            const resp = await authFetch(`http://localhost:5000/api/roles/${rolSeleccionado.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: vals.nombre,
                    description: vals.descripcion,
                    permissions: vals.permisos,
                    status: vals.estado
                })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al actualizar rol');
            }
            const data = await resp.json();
            setRoles(prev => prev.map(r => r.id === rolSeleccionado.id ? data.role : r));
            return { success: true };
        } catch (e) {
            return { success: false, error: e.message };
        }
    };

    const solicitarEliminarRol = (rolId) => {
        setDeleteTarget(rolId);
        setConfirmOpen(true);
    };

    const confirmarEliminarRol = async () => {
        const rolId = deleteTarget;
        setConfirmOpen(false);
        setDeleteTarget(null);
        if (!rolId) return;
        try {
            const resp = await authFetch(`http://localhost:5000/api/roles/${rolId}`, { method: 'DELETE' });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al eliminar rol');
            }
            setRoles(prev => prev.filter(r => r.id !== rolId));
        } catch (e) {
            alert(e.message);
        }
    };

    const cambiarEstado = async (rol) => {
        try {
            const nuevo = rol.status === 'Activo' ? 'Inactivo' : 'Activo';
            const resp = await authFetch(`http://localhost:5000/api/roles/${rol.id}/status`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status: nuevo })
            });
            if (!resp.ok) {
                const e = await resp.json().catch(() => ({}));
                throw new Error(e.error || 'Error al cambiar estado');
            }
            const data = await resp.json();
            setRoles(prev => prev.map(r => r.id === rol.id ? data.role : r));
        } catch (e) {
            alert(e.message);
        }
    };

    if (cargando) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center">
                    <Loader className="w-12 h-12 animate-spin mx-auto mb-4" />
                    <p className="text-gray-600">Cargando roles...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center text-red-600">
                    <p>Error: {error}</p>
                    <button onClick={() => window.location.reload()} className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg">
                        Reintentar
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <PageHeader
                title="Gestión de Roles"
                subtitle="Administra los roles y permisos del sistema"
                icon={Shield}
            >
                <button onClick={abrirCrear} className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-xl font-medium flex items-center gap-2 hover:from-blue-700 hover:to-purple-700 transition-all">
                    <Plus className="w-4 h-4" />
                    Nuevo Rol
                </button>
            </PageHeader>

            <Card>
                {/* Buscador */}
                <div className="flex flex-col sm:flex-row gap-4 mb-4">
                    <div className="flex-1 relative">
                        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <input
                            type="text"
                            placeholder="Buscar roles..."
                            value={busqueda}
                            onChange={(e) => setBusqueda(e.target.value)}
                            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        />
                    </div>
                </div>
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead>
                            <tr className="border-b border-gray-200">
                                <th className="text-left py-2 px-4 font-semibold text-gray-900">Nombre</th>
                                <th className="text-left py-2 px-4 font-semibold text-gray-900">Descripción</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Permisos</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Estado</th>
                                <th className="text-center py-2 px-4 font-semibold text-gray-900">Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {rolesFiltrados.length ? rolesFiltrados.map((rol, index) => (
                                <motion.tr
                                    key={rol.id}
                                    className="border-b border-gray-100 hover:bg-gray-50"
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: index * 0.05 }}
                                >
                                    <td className="py-2 px-4">{rol.name}</td>
                                    <td className="py-2 px-4 text-gray-600">{rol.description || '-'}</td>
                                    <td className="py-2 px-4 text-center">{(rol.permissions || []).length}</td>
                                    <td className="py-2 px-4 text-center">
                                        <div className="flex flex-col items-center gap-1">
                                            <InsigniaEstado estado={rol.status} />
                                            <button
                                                className={`px-2 py-1 rounded-lg text-white text-xs font-medium transition whitespace-nowrap ${rol.status === 'Activo' ? 'bg-red-500 hover:bg-red-600' : 'bg-blue-500 hover:bg-blue-600'}`}
                                                onClick={() => cambiarEstado(rol)}
                                            >
                                                {rol.status === 'Activo' ? 'Dar Baja' : 'Dar Alta'}
                                            </button>
                                        </div>
                                    </td>
                                    <td className="py-2 px-4">
                                        <div className="flex items-center justify-center gap-1 flex-wrap">
                                            <ActionButton
                                                color="blue"
                                                icon={Edit}
                                                onClick={() => abrirEditar(rol)}
                                                title="Editar rol"
                                            >
                                                Editar
                                            </ActionButton>
                                            <ActionButton
                                                color="red"
                                                icon={Trash2}
                                                onClick={() => solicitarEliminarRol(rol.id)}
                                                title="Eliminar rol"
                                            >
                                                Eliminar
                                            </ActionButton>
                                        </div>
                                    </td>
                                </motion.tr>
                            )) : (
                                <tr>
                                    <td colSpan={5} className="text-center py-6 text-gray-500">No se encontraron roles</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </Card>

            <ModalCrudGenerico
                isOpen={modalAbierto}
                mode={modoModal === 'crear' ? 'add' : 'edit'}
                title={modoModal === 'crear' ? 'Nuevo Rol' : 'Editar Rol'}
                icon={Shield}
                initialValues={valoresIniciales}
                size="md"
                validate={(vals) => {
                    if (!vals.nombre || !vals.nombre.trim()) return 'El nombre es requerido';
                    if (vals.nombre.length < 3) return 'El nombre debe tener al menos 3 caracteres';
                    if (!['Activo', 'Inactivo'].includes(vals.estado)) return 'Estado inválido';
                    return null;
                }}
                fields={[
                    { name: 'nombre', label: 'Nombre *', type: 'text', placeholder: 'Ej: Admin' },
                    { name: 'descripcion', label: 'Descripción', type: 'textarea', placeholder: 'Describe el rol' },
                    {
                        name: 'permisos',
                        label: 'Permisos',
                        type: 'custom',
                        render: (value, setValue, form, disabled) => (
                            <div key="permisos">
                                <label className="block text-sm font-medium text-gray-700 mb-2">Permisos</label>
                                <ListaPermisos value={value || []} onChange={setValue} disabled={disabled} columnas={2} />
                            </div>
                        )
                    },
                    {
                        name: 'estado',
                        label: 'Estado',
                        type: 'select',
                        options: [
                            { value: 'Activo', label: 'Activo' },
                            { value: 'Inactivo', label: 'Inactivo' }
                        ]
                    }
                ]}
                onSubmit={(vals) => (modoModal === 'crear' ? crearRol(vals) : editarRol(vals))}
                onClose={cerrarModal}
            />

            {/* Diálogo de confirmación */}
            <DialogoConfirmacion
                abierto={confirmOpen}
                titulo="Eliminar rol"
                mensaje="¿Estás seguro de eliminar este rol? Esta acción no se puede deshacer."
                onConfirmar={confirmarEliminarRol}
                onCancelar={() => { setConfirmOpen(false); setDeleteTarget(null); }}
                confirmText="Eliminar"
                cancelText="Cancelar"
            />
        </div>
    );
};

export default RolesPage;
